public class Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,j;
		for(i=9;i>=0;i--)
		{
			for(j=9;j>=i;j--)
			{
				System.out.print(i);
			}
			System.out.println();
		}
		for(i=0;i<=9;i++)
		{
			for(j=9;j>=i;j--)
			{
				System.out.print(i);
			}
			System.out.println();
		}

	}

}
